class TaskManager {
  constructor() {
    this.tasks = [];
  }

  addTask(task) {
    this.tasks.push(task);
  }

  getTasks() {
    return this.tasks;
  }
}

const express = require('express');
const router = express.Router();

router.get('/tasks', (req, res) => {
  res.json({ tasks: [] });
});

module.exports = router;
